html {
	scroll-behavior: smooth;
}

.amazon-product {
	background-color: #FFF;
	display: grid;
	grid-template-columns: 30% 70%;
	grid-gap: 10px;
	border: solid 1px #ccc;
	border-radius: 5px;
	box-shadow: 5px 5px 5px #FCBA02;
	margin-bottom: 20px;
	scroll-margin-top: 150px;
}

.amazon-product .product-image {
	color: #fff;
	border-radius: 5px;
	padding: 20px 0 20px 20px;
}

.amazon-product .product-image img {
	position: relative;
	top: 50%;
	transform: translateY(-50%);
}

.amazon-product .product-info {
	padding: 20px 20px 20px 0;
}

.amazon-product .button {
	background-color: #f0c14b;
	border: solid 1px #a88734;
	border-radius: 5px;
	color: #111;
	padding: 5px 10px;
	text-decoration: none;
}

.amazon-product .button:hover {
	background-color: #f7d787;
	border: solid 1px #a88734;
	color: #111;
}

/* Media queries for ipad and mobile */
@media only screen and (max-width: 1024px) {
	.amazon-product {
		grid-template-columns: 100%;
	}

	.amazon-product .product-image {
		padding: 20px;
	}

	.amazon-product .product-info {
		padding: 20px;
	}
}

